(window.webpackJsonp=window.webpackJsonp||[]).push([[110],{738:function(t,n){t.exports='\x3c!--\ntitle: 03-Flutter路由\nsort:\n--\x3e\n\n## 引入\n\n`list.dart`\n\n```dart\nimport \'package:flutter/material.dart\';\n\nclass ListPage extends StatelessWidget {\n  const ListPage({Key? key}) : super(key: key);\n\n  @override\n  Widget build(BuildContext context) {\n    \n    //定义列表 widget 的 list\n    List<Widget> list = <Widget>[];\n\n    // 数据\n    var data = [\n      {"id": 1, "title": "测试数据AAA", "subtitle": "ASDFASDFASDF"},\n      {"id": 2, "title": "测试数据bbb", "subtitle": "ASDFASDFASDF"},\n      {"id": 3, "title": "测试数据ccc", "subtitle": "ASDFASDFASDF"},\n      {"id": 4, "title": "测试数据eee", "subtitle":"ASDFASDFASDF"},\n    ];\n\n    // 构造列表 ListTile 组件 list\n    for (var item in data) {\n      list.add(ListTile(\n          title: Text(item["title"] as String,\n              style: const TextStyle(fontSize: 18.0)),\n          subtitle: Text(item["subtitle"] as String),\n          leading: const Icon(Icons.fastfood, color: Colors.orange),\n          trailing: const Icon(Icons.keyboard_arrow_right)));\n    }\n\n    // 返回整个页面\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text("List Page"),\n      ),\n      body: Center(\n          child: ListView(\n        children: list,\n      )),\n    );\n  }\n}\n```\n\n`main.dart`\n\n```dart\nimport \'list.dart\';\n\nonPressed: () {\n  // 路由跳转\n  Navigator.push(context, MaterialPageRoute(builder: (context) {\n    return const ListPage();\n  }));\n},\n```\n\n## \n\n## 定义路由\n\n`main.dart`\n\n```dart\nimport \'package:flutter/material.dart\';\nimport \'hello.dart\';\nimport \'list.dart\';\nimport \'detail.dart\';\n\nvoid main() => runApp(const MyApp());\n\nclass MyApp extends StatelessWidget {\n  const MyApp({Key? key}) : super(key: key);\n\n  // This widget is the root of your application.\n  @override\n  Widget build(BuildContext context) {\n    // Material 主题\n    return MaterialApp(\n      title: \'Flutter Demo\',\n      //路由表定义\n      routes: {\n        "Hello": (context) => const Hello(),\n        "ListPage": (context) => const ListPage(),\n        "DetailPage": (context) => const DetailPage(),\n      },\n      home: const MyHomePage(),\n    );\n  }\n}\n\nclass MyHomePage extends StatefulWidget {\n  const MyHomePage({Key? key}) : super(key: key);\n\n  @override\n  MyHomePageState createState() => MyHomePageState();\n}\n\nclass MyHomePageState extends State<MyHomePage> {\n  var count = 0;\n\n  final ButtonStyle flatButtonStyle = TextButton.styleFrom(\n    primary: Colors.white,\n    backgroundColor: Colors.blue,\n    // 圆角\n    shape: const RoundedRectangleBorder(\n      borderRadius: BorderRadius.all(Radius.circular(10.0)),\n    ),\n  );\n\n  @override\n  Widget build(BuildContext context) {\n    // Scaffold: 页面脚手架\n    // 包含 appBar, body, floatingActionButton 等\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text("我是Title"),\n      ),\n      body: Center(\n          child: Column(children: <Widget>[\n        TextButton(\n            onPressed: () {\n              // 路由跳转\n              Navigator.pushNamed(context, \'Hello\');\n            },\n            child: const Text("Hello")),\n        TextButton(\n          style: flatButtonStyle,\n          onPressed: () {\n            // 路由跳转\n            Navigator.pushNamed(context, \'ListPage\');\n          },\n          child: const Text("List"),\n        ),\n      ])),\n    );\n  }\n}\n```\n\n## 路由传参\n\n`list.dart`\n\n```\nimport \'package:flutter/material.dart\';\n\nclass ListPage extends StatelessWidget {\n  const ListPage({Key? key}) : super(key: key);\n\n  @override\n  Widget build(BuildContext context) {\n    //定义列表 widget 的 list\n    List<Widget> list = <Widget>[];\n\n    // 数据\n    var data = [\n      {"id": 1, "title": "测试数据AAA", "subtitle": "ASDFASDFASDF"},\n      {"id": 2, "title": "测试数据bbb", "subtitle": "ASDFASDFASDF"},\n      {"id": 3, "title": "测试数据ccc", "subtitle": "ASDFASDFASDF"},\n      {"id": 4, "title": "测试数据eee", "subtitle": "ASDFASDFASDF"},\n    ];\n\n    // 构造列表 ListTile 组件 list\n    for (var item in data) {\n      list.add(ListTile(\n        title: Text(item["title"] as String,\n            style: const TextStyle(fontSize: 18.0)),\n        subtitle: Text(item["subtitle"] as String),\n        leading: const Icon(Icons.fastfood, color: Colors.orange),\n        trailing: const Icon(Icons.keyboard_arrow_right),\n        onTap: () {\n          // 点击的时候，进行路由跳转传参\n          Navigator.pushNamed(context, "DetailPage", arguments: item);\n        },\n      ));\n    }\n\n    // 返回整个页面\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text("List Page"),\n      ),\n      body: Center(\n          child: ListView(\n        children: list,\n      )),\n    );\n  }\n}\n```\n\n`detail.dart`\n\n```dart\nimport \'package:flutter/material.dart\';\n\nclass DetailPage extends StatelessWidget {\n  const DetailPage({Key? key}) : super(key: key);\n\n  @override\n  Widget build(BuildContext context) {\n    // 获取路由传参\n    final args = ModalRoute.of(context)?.settings.arguments as Map;\n\n    return Scaffold(\n        appBar: AppBar(\n          title: const Text("Detail Page"),\n        ),\n        body: Column(\n          children: <Widget>[\n            const Text("我是Detail页面"),\n            Text("id:${args[\'id\']}"),\n            Text("id:${args[\'title\']}"),\n            Text("id:${args[\'subtitle\']}")\n          ],\n        ));\n  }\n}\n```\n\n'}}]);