(window.webpackJsonp=window.webpackJsonp||[]).push([[9],{637:function(n,a){n.exports='\x3c!--\ntitle: 01-常用命令\nsort:\n--\x3e\n\n## 基础命令\n\n```bash\npwd # 当前目录\n\nls <path> # 列出全部目录\n  -lah # 详细信息|全部|文件大小\n\ncp <source> <target> # 复制目录\nmv <source> <target> # 移动目录\n  -riuv # 递归|覆盖提示|不覆盖|实时信息\n\nmkdir -R <path> # 新建目录\nrm <path> # 删除文件/目录\n  -if # 删除提示|强制删除\n\nln <source> <target> # 创建链接\n  -s # 创建软链接\n\nman <command> # 命令简介\n```\n\n## 工具\n\n```bash\n# 时间\ndate # 当前日期\n# 格式化输出\necho $(date "+%Y_%m_%d_%H_%M_%S")\n\ncal # 日历\n\n# 文件\nfile <file> # 文件信息\ncat <file> # 文件内容\nless <file> # 文件内容\n\n# 搜索\ngrep <content> # 搜索内容\n  -niv # 行数|忽略大小写|取反\ngrep err.log -e painc\n\nfind <path> -name <file> # 计算机内搜索内容\n  -name # 文件名\n  -size # 大小\n    +15K # 大于15KB\n    -1M # 小于1MB\n  -type # 文件类型\n  -exec # 对搜索出的文件运行命令\n    rm {} \\; # 删除\n\n# 归档及压缩\ngzip -d # 解压缩\ntar # 归档|解档|压缩|解压\n  -cvf # 归档\n  -xvf # 解档\n  -zcvf <target> <source> # 压缩\n  -zxvf # 解压\n\n# 信息\ntype <command> # 命令详情\nwhich <command> # 命令程序位置\nwhatis <command> # 命令简洁说明\nalias <alias>=<command> # 未命令创建别名\n```\n\n## 软件安装\n\n```bash\n# 搜索\napt search <package>\n# 安装\napt install <package>\n# 卸载\napt remove <package>\n# 彻底卸载\napt-get remove --purge\n\n# 安装软件包\ndpkg -i *.deb\n```\n'}}]);