(window.webpackJsonp=window.webpackJsonp||[]).push([[98],{726:function(n,e){n.exports="\x3c!--\ntitle: 08-Axios\nsort:\n--\x3e\n\n## 使用\n\n```\nyarn add axios\n```\n\n### `request.js`\n\n```js\nimport axios from 'axios'\nimport router from '@/router/router'\n\nasync function request(url, options) {\n  // axios 实例\n  const service = axios.create({\n    baseURL: import.meta.env.VITE_SERVER_URL, // base url\n    timeout: 12000, // 超时时间\n  })\n  // 请求拦截\n  service.interceptors.request.use((config) => {\n    // 设置请求头等信息\n    if (options && options.body) {\n      config.data = options.body\n    }\n    return config\n  })\n  // 返回拦截\n  service.interceptors.response.use(\n    (res) => {\n      // 返回数据的处理\n      return res.data\n    },\n    (err) => {\n      // 失败的处理\n      alert(\"网络请求失败\")\n    }\n  )\n  return service(url, options)\n}\n\nexport default request\n```\n\n## 请求\n\n```js\n// Get\nrequest('/exam-info/search', {\n  params: { 'student_id': student_id.value },\n}).then((data) => {\n  console.log(data)\n})\n\n// Post\nrequest('url', {\n  method: 'post',\n  data: info,\n}).then((data) => {\n  console.log(data)\n})\n```\n\n"}}]);